int countGoodTriplets(vector<int> &firstArray, vector<int> &secondArray)
{
    int arraySize = firstArray.size();
    vector<int> firstArrayPosition(arraySize), secondArrayPosition(arraySize);

    for(int i = 0; i < arraySize; ++i)
    {
        firstArrayPosition[firstArray[i]] = i;
        secondArrayPosition[secondArray[i]] = i;
    }

    int tripletCount = 0;

    for(int firstIndex = 0; firstIndex < arraySize; ++firstIndex)
    {
        for(int secondIndex = firstIndex + 1; secondIndex < arraySize; ++secondIndex)
        {
            for(int thirdIndex = secondIndex + 1; thirdIndex < arraySize; ++thirdIndex)
            {
                if(firstArrayPosition[firstIndex] < firstArrayPosition[secondIndex] &&
                   firstArrayPosition[secondIndex] < firstArrayPosition[thirdIndex] &&
                   secondArrayPosition[firstIndex] < secondArrayPosition[secondIndex] &&
                   secondArrayPosition[secondIndex] < secondArrayPosition[thirdIndex])
                {
                    tripletCount++;
                }
            }
        }
    }

    return tripletCount;
}

