#include <bits/stdc++.h>
using namespace std;

int rearrangeDigits(int number)
{
    if(number == 0)
        return 0;
    string stringNumber= to_string(number);
    vector<char> digits(stringNumber.begin(), stringNumber.end());
    sort(digits.begin(), digits.end());

    if(stringNumber[0] != '-' && digits[0] == '0')
    {
        auto nonzeroDigitIterator= find_if(digits.begin(), digits.end(), [](char character)
                                            { return character != '0'; });
        swap(digits[0], *nonzeroDigitIterator);
    }

    string result(digits.begin(), digits.end());
    return stoi(result);
}

