int maxNumberOfCopies(string sourceString, string targetString)
{
    vector<int> sourceCounts(26, 0);
    vector<int> targetCounts(26, 0);

    for (int i = 0; i < sourceString.size(); ++i)
        sourceCounts[sourceString[i] - 'a']++;

    for (int i = 0; i < targetString.size(); ++i)
        targetCounts[targetString[i] - 'a']++;

    int maxCopies = INT32_MAX;
    for (int index = 0; index < 26; index++)
    {
        if (targetCounts[index] > 0)
            maxCopies = min(maxCopies, sourceCounts[index] / targetCounts[index]);
    }
    return maxCopies;
}

