int minNumberOfBeansToRemove(const vector<int> &beanCounts)
{
    unordered_map<int, int> beanFrequency;
    for(int bean : beanCounts)
    {
        beanFrequency[bean]++;
    }

    int minBeansToRemove = beanCounts.size();
    for(auto it = beanFrequency.begin(); it != beanFrequency.end(); ++it)
    {
        const auto &currentBean = *it;
        int beansToRemove = 0;
        for(auto jt = beanFrequency.begin(); jt != beanFrequency.end(); ++jt)
        {
            const auto &otherBean = *jt;
            if(otherBean.first != currentBean.first)
            {
                beansToRemove += otherBean.second * max(0, otherBean.first - currentBean.first);
            }
        }
        minBeansToRemove = min(minBeansToRemove, beansToRemove);
    }

    return minBeansToRemove;
}

