using namespace std;

int maxAndSum(vector<int> &numbers, int numSlots)
{
    vector<int> slot(numSlots + 1, 0);
    for (int i = 0; i < numbers.size(); ++i)
    {
        int number = numbers[i];
        for (int slotIndex = numSlots; slotIndex > 0; slotIndex--)
        {
            int newValue = number & slotIndex;
            if (newValue > slot[slotIndex])
                slot[slotIndex] = newValue;
        }
    }

    return accumulate(slot.begin(), slot.end(), 0);
}

