using namespace std;

int minimumTimeForTrips(vector<int> &travelTimes, int totalTrips)
{
    sort(travelTimes.begin(), travelTimes.end());

    int lowerBound = 0, upperBound = travelTimes.back() * totalTrips;
    while(lowerBound < upperBound)
    {
        int midTime = lowerBound + (upperBound - lowerBound) / 2;
        int completedTrips = 0;
        for(int i = 0; i < travelTimes.size(); ++i)
        {
            completedTrips += midTime / travelTimes[i];
        }
        if(completedTrips < totalTrips)
        {
            lowerBound = midTime + 1;
        }
        else
        {
            upperBound = midTime;
        }
    }

    return lowerBound;
}

