int minRaceTime(vector<vector<int>> &tireData, int changeTime, int numLaps)
{
    int tireCount= tireData.size();
    vector<int> lapTimes(tireCount);
    for(int i= 0; i < tireCount; ++i)
        lapTimes[i]= tireData[i][0] + (numLaps - 1) * tireData[i][1];
    int minTime= *min_element(lapTimes.begin(), lapTimes.end()) + (numLaps - 1) * changeTime;

    for(int i= 0; i < tireCount; ++i)
        for(int j= 0; j < tireCount; ++j)
            if(i != j)
                minTime= min(minTime, lapTimes[i] + lapTimes[j] - tireData[i][0] + (numLaps - 2) * changeTime);
    return minTime;
}

