class TextEditor
{
    string editorText;
    int cursorPosition;

  public:
    TextEditor()
    {
        editorText= "";
        cursorPosition= 0;
    }

    void addText(string newText)
    {
        editorText.insert(cursorPosition, newText);
        cursorPosition+= newText.size();
    }

    int deleteText(int numberOfCharacters)
    {
        int deletedCharacters= min(numberOfCharacters, cursorPosition);
        editorText.erase(cursorPosition - deletedCharacters, deletedCharacters);
        cursorPosition-= deletedCharacters;
        return deletedCharacters;
    }

    string cursorLeft(int k)
    {
        cursorPosition-= min(k, cursorPosition);
        return editorText.substr(max(0, cursorPosition - 10), min(10, cursorPosition));
    }

    string cursorRight(int k)
    {
        cursorPosition+= min(k, (int)editorText.size() - cursorPosition);
        return editorText.substr(max(0, cursorPosition - 10), min(10, cursorPosition));
    }
};

