using namespace std;

int minHeightJump(const vector<int> &heights, const vector<int> &costs)
{
    int numberOfHeights = heights.size();
    vector<int> minimumCosts(numberOfHeights, 1e9);

    minimumCosts[0] = 0;
    for(int currentIndex = 1; currentIndex < numberOfHeights; ++currentIndex)
    {
        int previousIndex = currentIndex - 1;
        while(previousIndex >= 0)
        {
            if((heights[previousIndex] <= heights[currentIndex] && (previousIndex == 0 || heights[previousIndex - 1] < heights[currentIndex])) ||
               (heights[previousIndex] > heights[currentIndex] && (previousIndex == 0 || heights[previousIndex - 1] >= heights[currentIndex])))
            {
                minimumCosts[currentIndex] = min(minimumCosts[currentIndex], minimumCosts[previousIndex] + costs[currentIndex]);
            }
            --previousIndex;
        }
    }
    return minimumCosts[numberOfHeights - 1];
}

