class ListNode
{
  public:
    int value;
    ListNode *nextNode;
    ListNode(int x) : value(x), nextNode(NULL) {}
};

ListNode *mergeNodesBetweenZeros(ListNode *headNode)
{
    ListNode *dummyNode= new ListNode(0);
    dummyNode->nextNode= headNode;
    ListNode *previousNode= dummyNode;
    ListNode *currentNode= headNode;

    while(currentNode)
    {
        if(currentNode->value == 0)
        {
            int temporarySum= 0;
            while(currentNode->nextNode && currentNode->nextNode->value != 0)
            {
                temporarySum+= currentNode->nextNode->value;
                currentNode= currentNode->nextNode;
            }
            previousNode->nextNode= new ListNode(temporarySum);
            previousNode= previousNode->nextNode;
            currentNode= currentNode->nextNode;
        }
    }

    return dummyNode->nextNode;
}

