#include <bits/stdc++.h>
using namespace std;

string repeatLimitedString(string inputString, int repeatLimit)
{
    string outputString = "";
    sort(inputString.begin(), inputString.end(), greater<char>());

    for (int i = 0; i < inputString.size(); ++i)
    {
        char currentChar = inputString[i];
        size_t charPosition = outputString.find(currentChar);

        if (charPosition == string::npos)
        {
            outputString.push_back(currentChar);
        }
        else
        {
            int charCount = 1;

            while (charPosition + charCount < outputString.size() && outputString[charPosition + charCount] == currentChar)
            {
                charCount++;
            }

            if (charCount < repeatLimit)
            {
                outputString.insert(charPosition + charCount, 1, currentChar);
            }
