int minVisibleWhiteTiles(const string &floor, int numCarpets, int carpetLength)
{
    int totalTiles = floor.size();
    int visibleWhiteCount = 0;
    for(int i = 0; i < totalTiles; i++)
        visibleWhiteCount += (floor[i] == '1');

    int coveredTiles = 0;
    for(int i = 0; i < totalTiles; i++)
    {
        if(floor[i] == '1')
        {
            int startIndex = max(0, i - carpetLength + 1);
            int endIndex = min(totalTiles - carpetLength, i);
            int maxCover = 0;
            for(int firstTile = startIndex; firstTile <= endIndex; firstTile++)
            {
                int cover = count(floor.begin() + firstTile, floor.begin() + firstTile + carpetLength, '1');
                maxCover = max(maxCover, cover);
            }

            coveredTiles += maxCover;
            --numCarpets;
            if(numCarpets == 0)
                break;
        }
    }

    return visibleWhiteCount - coveredTiles;
}

