int shortestPath(int numNodes, vector<vector<int>> &edges, int source1, int source2, int destination)
{
    vector<vector<pair<int, int>>> adjacencyList(numNodes);

    for (int i = 0; i < edges.size(); ++i)
    {
        const auto &edge = edges[i];
        adjacencyList[edge[0]].emplace_back(edge[1], edge[2]);
    }

    auto dijkstra = [&](int source)
    {
        vector<int> distances(numNodes, INT_MAX);
        priority_queue<pair<int, int>, vector<pair<int, int>>, greater<pair<int, int>>> priorityQueue;

        distances[source] = 0;
        priorityQueue.emplace(0, source);

        while (!priorityQueue.empty())
        {
            int currentNode = priorityQueue.top().second;
            int currentDistance = priorityQueue.top().first;
            priorityQueue.pop();

            if (currentDistance != distances[currentNode])
                continue;

            for (int i = 0; i < adjacencyList[currentNode].size(); ++i)
            {
                const auto &edge = adjacencyList[currentNode][i];
                int neighbor = edge.first;
                int weight = edge.second;

                if (distances[currentNode] + weight < distances[neighbor])
                {
                    distances[neighbor] = distances[currentNode] + weight;
                    priorityQueue.emplace(distances[neighbor], neighbor);
                }
            }
        }

        return distances;
    };

    vector<int> source1ToAllDistances = dijkstra(source1);
    vector<int> source2ToAllDistances = dijkstra(source2);

    int minimumWeight = INT_MAX;
    for (int i = 0; i < edges.size(); ++i)
    {
        const auto &edge = edges[i];
        int u = edge[0];
        int v = edge[1];
        int w = edge[2];
        minimumWeight = min(minimumWeight, source1ToAllDistances[u] + w + source2ToAllDistances[v]);
    }

    return minimumWeight == INT_MAX ? -1 : minimumWeight;
}

