int depthFirstSearch(int currentNode, int parentNode, vector<int> &nodeXorValues, vector<vector<int>> &adjacencyList)
{
    int currentXorValue = nodeXorValues[currentNode];
    for (int childNode : adjacencyList[currentNode])
    {
        if (childNode != parentNode)
        {
            currentXorValue ^= depthFirstSearch(childNode, currentNode, nodeXorValues, adjacencyList);
        }
    }
    return currentXorValue;
}

int minimumScore(vector<int> &nodeValues, vector<vector<int>> &edgeList)
{
    int nodeCount = nodeValues.size();
    vector<vector<int>> adjacencyList(nodeCount);

    for (int i = 0; i < edgeList.size(); ++i)
    {
        adjacencyList[edgeList[i][0]].push_back(edgeList[i][1]);
        adjacencyList[edgeList[i][1]].push_back(edgeList[i][0]);
    }

    vector<int> nodeXorValues(nodeCount);
    for (int i = 0; i < nodeCount; ++i)
    {
        nodeXorValues[i] = depthFirstSearch(i, -1, nodeValues, adjacencyList);
    }

    int minimumDifference = INT_MAX;
    for (int i = 0; i < nodeCount; ++i)
    {
        for (int j = i + 1; j < nodeCount; ++j)
        {
            int xorValue1 = nodeXorValues[i];
            int xorValue2 = nodeXorValues[j] ^ nodeValues[i];
            int xorValue3 = nodeValues[i] ^ nodeValues[j];
            int maxXorValue = max({xorValue1, xorValue2, xorValue3});
            int minXorValue = min({xorValue1, xorValue2, xorValue3});
            minimumDifference = min(minimumDifference, maxXorValue - minXorValue);
        }
    }

    return minimumDifference;
}

