int maxBeauty(vector<int> &flowerCounts, int availableNewFlowers, int beautyTarget, int fullBeauty, int partialBeauty)
{
    int totalBeauty = 0;
    for (int i = 0; i < flowerCounts.size(); ++i)
        totalBeauty += (flowerCounts[i] >= beautyTarget) ? fullBeauty : partialBeauty * flowerCounts[i];

    sort(flowerCounts.begin(), flowerCounts.end());

    for (int i = 0; i < flowerCounts.size() && availableNewFlowers > 0; ++i)
    {
        int flowersNeeded = beautyTarget - flowerCounts[i];
        if (flowersNeeded <= availableNewFlowers)
        {
            totalBeauty += fullBeauty - partialBeauty * flowerCounts[i];
            flowerCounts[i] = beautyTarget;
            availableNewFlowers -= flowersNeeded;
        }
    }

    return totalBeauty;
}

