{
    sort(busTimes.begin(), busTimes.end());
    sort(passengerTimes.begin(), passengerTimes.end());

    int lastPassengerIndex = 0;
    for (int i = 0; i < busTimes.size(); ++i)
    {
        int busTime = busTimes[i];
