int minSumOfSquaredDifference(vector<int> &firstArray, vector<int> &secondArray, int availableSteps1, int availableSteps2)
{
    int64_t totalSquaredDifference= 0;
    for(int index= 0; index < firstArray.size(); ++index)
    {
        int difference= abs(firstArray[index] - secondArray[index]);
        int stepsUsed= min(availableSteps1 + availableSteps2, difference);
        difference-= stepsUsed;
        availableSteps1-= min(availableSteps1, stepsUsed);
        totalSquaredDifference+= 1LL * difference * difference;
    }
    return (int)totalSquaredDifference;
}

