{
    int arraySize= numArray.size();
    int leftIndex= 0, rightIndex= 0, currentSum= 0;
    int maxLength= -1;

    while(rightIndex < arraySize)
    {
        currentSum+= numArray[rightIndex];
        while(currentSum * (rightIndex - leftIndex + 1) < threshold * (rightIndex - leftIndex + 1))
        {
            currentSum-= numArray[leftIndex];
            leftIndex++;
        }
        maxLength= max(maxLength, rightIndex - leftIndex + 1);
        rightIndex++;
    }

    return maxLength;
