bool canTransform(string start, string target)
{
    int length= start.length();
    int startPointer= 0, targetPointer= 0;

    while(startPointer < length && targetPointer < length)
    {
        // Move pointers until there is a letter.
        while(startPointer < length && start[startPointer] == '_')
            startPointer++;
        while(targetPointer < length && target[targetPointer] == '_')
            targetPointer++;

        if(startPointer == length || targetPointer == length)
            break;

        // Check if the chars at pointers are the same.
        if(start[startPointer] != target[targetPointer])
            return false;

        if(start[startPointer] == 'L' && startPointer < targetPointer)
            return false;
        if(start[startPointer] == 'R' && startPointer > targetPointer)
            return false;

        startPointer++;
        targetPointer++;
    }

    return startPointer == length && targetPointer == length;
}

