using namespace std;

int calculateNumberOfArrays(int arrayLength, int maximumValue)
{
    const int modulo= 1e9 + 7;
    vector<int> dp(maximumValue + 1, 0);

    for(int currentValue= 1; currentValue <= maximumValue; currentValue++)
    {
        dp[currentValue]= currentValue;
    }

    for(int i= 1; i < arrayLength; i++)
    {
        for(int j= maximumValue; j > 0; j--)
        {
            dp[j]= (dp[j] + dp[j - 1]) % modulo;
        }
    }

    return dp[maximumValue];
}

