int calculateMinSwaps(vector<int> &numberArray)
{
    int arraySize= numberArray.size();
    int minPosition= 0, maxPosition= 0, swapCount= 0;

    for(int i= 1; i < arraySize; i++)
    {
        if(numberArray[i] <= numberArray[minPosition])
            minPosition= i;
        if(numberArray[i] >= numberArray[maxPosition])
            maxPosition= i;
    }

    swapCount= minPosition + (arraySize - 1 - maxPosition);
    if(minPosition < maxPosition)
        swapCount--;

    return swapCount;
}

