    int minPosition= 0, maxPosition= 0, swapCount= 0;

    for(int i= 1; i < arraySize; i++)
    {
        if(number_array[i] <= number_array[minPosition])
            minPosition= i;
        if(number_array[i] >= number_array[maxPosition])
            maxPosition= i;
    }

    swapCount= minPosition + (arraySize - 1 - maxPosition);
    if(minPosition < maxPosition)
        swapCount--;

    return swapCount;
}

