{
    vector<int> result;
    vector<pair<int, int>> indexPairs(numberStrings.size());
    for(size_t index= 0; index < numberStrings.size(); ++index)
    {
        indexPairs[index]= {stoi(numberStrings[index]), index};
    }

    for(size_t qIndex = 0; qIndex < queries.size(); ++qIndex)
    {
        auto k = queries[qIndex][0];
        auto trimLength = queries[qIndex][1];
        for(size_t index = 0; index < numberStrings.size(); ++index)
        {
            indexPairs[index].first = stoi(numberStrings[index].substr(numberStrings[index].size() - trimLength));
        }
        sort(indexPairs.begin(), indexPairs.end());
        result.push_back(indexPairs[k - 1].second);
    }

    return result;
}

