int minDeletions(vector<int> &inputNumbers, vector<int> &divisorNumbers)
{
    int minimumDeletions = INT_MAX;
    for (int i = 0; i < inputNumbers.size(); ++i)
    {
        int currentNumber = inputNumbers[i];
        int countOccurrences = 0;
        bool isDivisible = true;
        for (int j = 0; j < divisorNumbers.size(); ++j)
        {
            int divisor = divisorNumbers[j];
            if (divisor % currentNumber != 0)
            {
                isDivisible = false;
                break;
            }
        }
        if (isDivisible)
        {
            for (int j = 0; j < inputNumbers.size(); ++j)
            {
                int n = inputNumbers[j];
                if (n == currentNumber)
                {
                    countOccurrences++;
                }
            }
            minimumDeletions = min(minimumDeletions, (int)inputNumbers.size() - countOccurrences);
        }
    }
    return minimumDeletions == INT_MAX ? -1 : minimumDeletions;
}

