int findShortestUnrollableSequence(vector<int> &rolls, int k)
{
    vector<int> count(k + 1);
    for(int i = 0; i < rolls.size(); ++i)
    {
        count[rolls[i]]++;
    }

    int sequenceLength = 1;
    while(true)
    {
        int maxSubsequences = 1;
        for(int i = 1; i <= k; i++)
        {
            maxSubsequences *= count[i] + (sequenceLength == 1);
        }

        if(maxSubsequences < sequenceLength * rolls.size())
        {
            return sequenceLength;
        }

        sequenceLength++;
    }
}

