    int maxBooks= 0;

    for(int leftIndex= 0; leftIndex < totalBooks; ++leftIndex)
    {
        int rightIndex= leftIndex + 1, booksTaken= bookCounts[leftIndex], minBooksTaken= bookCounts[leftIndex];
        maxBooks= max(maxBooks, booksTaken);

        while(rightIndex < totalBooks)
        {
            minBooksTaken= min(minBooksTaken, bookCounts[rightIndex]);
            booksTaken+= minBooksTaken;
            maxBooks= max(maxBooks, booksTaken);

            if(minBooksTaken <= 1)
                break; // Can't decrease further
            ++rightIndex;
        }
    }

    return maxBooks;
}

