int maxGroups(vector<int> &grades)
{
    int gradesCount= grades.size();
    vector<int> dynamicProgramming(gradesCount, 1);

    sort(grades.begin(), grades.end());

    for(int currentIndex= 0; currentIndex < gradesCount; ++currentIndex)
    {
        for(int previousIndex= 0; previousIndex < currentIndex; ++previousIndex)
        {
            if(grades[currentIndex] + dynamicProgramming[previousIndex] * grades[previousIndex] > (currentIndex + 1) * grades[previousIndex])
            {
                dynamicProgramming[currentIndex]= dynamicProgramming[previousIndex] + 1;
            }
        }
