int minDaysToCompleteTasks(vector<int> &taskList, int spaceBetweenTasks)
{
    unordered_map<int, int> taskLastCompleteDay;
    int totalDays = 0;

    for (int i = 0; i < taskList.size(); ++i)
    {
        int currentTask = taskList[i];
        if (taskLastCompleteDay.find(currentTask) != taskLastCompleteDay.end())
        {
            int passedDays = totalDays - taskLastCompleteDay[currentTask];
            if (passedDays < spaceBetweenTasks)
            {
                totalDays += spaceBetweenTasks - passedDays;
            }
        }
        taskLastCompleteDay[currentTask] = ++totalDays;
    }

    return totalDays;
}

