int maxNodesWithoutRestricted(int totalNodes, const vector<vector<int>> &edges, const vector<int> &restrictedNodes)
{
    unordered_map<int, vector<int>> adjacencyList;
    set<int> blockedNodes(restrictedNodes.begin(), restrictedNodes.end());

    for (const auto &edge : edges)
    {
        adjacencyList[edge[0]].push_back(edge[1]);
        adjacencyList[edge[1]].push_back(edge[0]);
    }

    int maxNodesCount = 0;
    function<void(int, int, int)> depthFirstSearch = [&](int currentNode, int currentDepth, int parentNode)
    {
        if (blockedNodes.count(currentNode))
            return;
        maxNodesCount = max(maxNodesCount, currentDepth);
        for (int i = 0; i < adjacencyList[currentNode].size(); ++i)
        {
            int childNode = adjacencyList[currentNode][i];
            if (childNode != parentNode)
                depthFirstSearch(childNode, currentDepth + 1, currentNode);
        }
    };

    depthFirstSearch(0, 1, -1);
    return maxNodesCount;
}

