int longestIdealString(const string &inputString, int allowedDifference)
{
    vector<int> dynamicProgramming(26, 0);

    for (int i = 0; i < inputString.size(); ++i)
    {
        char currentChar = inputString[i];
        int currentLength = 1;
        for (int index = 0; index < 26; ++index)
