        int startIndex = shift[0], endIndex = shift[1], direction = shift[2];
        int value = (direction == 1) ? 1 : -1;
        shiftValues[startIndex] += value;
        if (endIndex + 1 < inputString.size())
            shiftValues[endIndex + 1] -= value;
