vector<int> maxSegmentSumAfterQueries(vector<int> &numberArray, vector<int> &removeQueries)
{
    int arraySize= numberArray.size();
    vector<int> answer(arraySize);
    set<int> removedIndices;
    removedIndices.insert(-1);
    removedIndices.insert(arraySize);

    int currentSegmentSum= 0, maximumSegmentSum= 0;
    for(int index= 0; index < arraySize; ++index)
    {
        if(numberArray[index] > 0)
            currentSegmentSum+= numberArray[index];
        else
        {
            maximumSegmentSum= max(maximumSegmentSum, currentSegmentSum);
            currentSegmentSum= 0;
        }
    }
    maximumSegmentSum= max(maximumSegmentSum, currentSegmentSum);

    for(int queryIndex= 0; queryIndex < arraySize; ++queryIndex)
    {
        int removeIndex= removeQueries[queryIndex];
        answer[queryIndex]= maximumSegmentSum;
        numberArray[removeIndex]= -numberArray[removeIndex];

        int leftBoundaryIndex= *removedIndices.upper_bound(removeIndex);
        int rightBoundaryIndex= *prev(removedIndices.upper_bound(removeIndex));

        int leftSegmentSum= 0, rightSegmentSum= 0;
        for(int j= leftBoundaryIndex - 1; j >= 0; --j)
        {
            if(numberArray[j] < 0)
                leftSegmentSum-= numberArray[j];
            else
                break;
        }
        for(int j= rightBoundaryIndex + 1; j < arraySize; ++j)
        {
            if(numberArray[j] < 0)
                rightSegmentSum-= numberArray[j];
            else
                break;
        }

        maximumSegmentSum-= max(0, leftSegmentSum - (-numberArray[removeIndex]));
        maximumSegmentSum-= max(0, rightSegmentSum - (-numberArray[removeIndex]));
        maximumSegmentSum+= max(0, leftSegmentSum + rightSegmentSum - (-numberArray[removeIndex]));

        removedIndices.insert(removeIndex);
    }

    return answer;
}

