int minTrainingHours(int initialEnergy, int initialExperience, vector<int> &energyRequirements, vector<int> &experienceRequirements)
{
    int numberOfChallenges = energyRequirements.size();
    int minimumTrainingHours = 1e9;
    for(int experienceGain = 0; experienceGain <= initialEnergy + initialExperience; ++experienceGain)
    {
        int trainingHours = experienceGain;
        int remainingEnergy = initialEnergy - experienceGain;
        int currentExperience = initialExperience + experienceGain;

        for(int challengeIndex = 0; challengeIndex < numberOfChallenges; ++challengeIndex)
        {
            while(currentExperience <= experienceRequirements[challengeIndex] || remainingEnergy <= energyRequirements[challengeIndex])
            {
                remainingEnergy--;
                trainingHours++;
            }
            remainingEnergy -= energyRequirements[challengeIndex];
            currentExperience += experienceRequirements[challengeIndex];
        }
        minimumTrainingHours = min(minimumTrainingHours, trainingHours);
    }
    return minimumTrainingHours;
}

