int findMedian(vector<vector<int>> &numberGrid)
{
    int rowCount= numberGrid.size(), columnCount= numberGrid[0].size();
    int lowerBound= 1, upperBound= 1e6;

    while(lowerBound < upperBound)
    {
        int midPoint= lowerBound + (upperBound - lowerBound) / 2;
        int elementCount= 0;
        for(int i= 0; i < rowCount; ++i)
        {
            elementCount+= upper_bound(numberGrid[i].begin(), numberGrid[i].end(), midPoint) - numberGrid[i].begin();
        }

        if(elementCount < (rowCount * columnCount) / 2 + 1)
        {
            lowerBound= midPoint + 1;
        }
        else
        {
            upperBound= midPoint;
        }
    }

    return lowerBound;
}

