vector<vector<int>> buildMatrix(int size, vector<vector<int>> &rowDependencies, vector<vector<int>> &colDependencies)
{
    vector<int> rowOrder(size + 1, 0), colOrder(size + 1, 0);

    for (int i = 0; i < rowDependencies.size(); ++i)
    {
        const auto &dependency = rowDependencies[i];
        if (rowOrder[dependency[0]] >= rowOrder[dependency[1]])
            return {};
        rowOrder[dependency[1]] = rowOrder[dependency[0]] + 1;
    }

    for (int i = 0; i < colDependencies.size(); ++i)
    {
        const auto &dependency = colDependencies[i];
        if (colOrder[dependency[0]] >= colOrder[dependency[1]])
            return {};
        colOrder[dependency[1]] = colOrder[dependency[0]] + 1;
    }

    vector<vector<int>> matrix(size, vector<int>(size));
    for (int index = 1; index <= size; ++index)
    {
        matrix[rowOrder[index]][colOrder[index]] = index;
    }

    return matrix;
}

