int maxConsecutiveRobots(vector<int> &chargeTimes, vector<int> &runningCosts, long long budget)
{
    int numberOfRobots= chargeTimes.size();
    int maxRobotsCount= 0;
    for(int startIndex= 0; startIndex < numberOfRobots; startIndex++)
    {
        int maxCharge= 0;
        long long totalRunningCost= 0;
        for(int endIndex= startIndex; endIndex < numberOfRobots; endIndex++)
        {
            maxCharge= max(maxCharge, chargeTimes[endIndex]);
            totalRunningCost+= runningCosts[endIndex];
            long long totalCost= maxCharge + totalRunningCost * (endIndex - startIndex + 1);
            if(totalCost <= budget)
                maxRobotsCount= max(maxRobotsCount, endIndex - startIndex + 1);
            else
                break;
        }
    }
    return maxRobotsCount;
}

