const int modulus= 1e9 + 7;
const int maxPosition= 1001;

int calculateWays(int currentPosition, int targetPosition, int stepsRemaining, vector<vector<int>> &memoization)
{
    if(stepsRemaining == 0)
        return currentPosition == targetPosition;
    if(currentPosition < 0 || targetPosition < 0 || currentPosition >= maxPosition || targetPosition >= maxPosition)
        return 0;

    if(memoization[currentPosition][stepsRemaining] != -1)
        return memoization[currentPosition][stepsRemaining];

    int moveLeft= calculateWays(currentPosition - 1, targetPosition, stepsRemaining - 1, memoization);
    int moveRight= calculateWays(currentPosition + 1, targetPosition, stepsRemaining - 1, memoization);

    return memoization[currentPosition][stepsRemaining]= (moveLeft + moveRight) % modulus;
}

int numberOfWays(int startPosition, int endPosition, int steps)
{
    vector<vector<int>> memoization(maxPosition, vector<int>(steps + 1, -1));
    return calculateWays(startPosition, endPosition, steps, memoization);
}

