    priority_queue<pair<int, int>, vector<pair<int, int>>, greater<>> roomQueue;
    vector<int> roomVisitCount(totalRooms);

    for (int i = 0; i < meetings.size(); ++i)
    {
        const auto &meeting = meetings[i];
        while (!roomQueue.empty() && roomQueue.top().first <= meeting[0])
        {
            ++roomVisitCount[roomQueue.top().second];
            roomQueue.pop();
        }
        roomQueue.push({meeting[1], roomQueue.size() % totalRooms});
    }
    int mostVisitedRoomIndex = 0;
    for (int index = 1; index < totalRooms; ++index)
    {
        if (roomVisitCount[index] > roomVisitCount[mostVisitedRoomIndex])
        {
            mostVisitedRoomIndex = index;
        }
    }
    return mostVisitedRoomIndex;
}

