using namespace std;

int minDays(const vector<int> &power)
{
    int numberOfPowers = power.size();
    int minimumDays = 1e9;
    vector<int> dynamicProgramming(1 << numberOfPowers, 1e9);

    dynamicProgramming[0] = 0;
    vector<int> sortedPower(power.begin(), power.end());
    sort(sortedPower.begin(), sortedPower.end());

    for(int currentMask = 0; currentMask < (1 << numberOfPowers); ++currentMask)
    {
        int powerSum = 0;
        for(int j = 0; j < numberOfPowers; ++j)
        {
            if(currentMask & (1 << j))
            {
                powerSum += sortedPower[j];
            }
        }
        int gain = 1 + __builtin_popcount(currentMask);
        for(int j = 0; j < numberOfPowers; ++j)
        {
            if(!(currentMask & (1 << j)) && gain > sortedPower[j])
            {
                dynamicProgramming[currentMask | (1 << j)] = min(dynamicProgramming[currentMask | (1 << j)], dynamicProgramming[currentMask] + 1);
            }
        }
    }
    return dynamicProgramming[(1 << numberOfPowers) - 1];
}

