int longestSubsequence(vector<int> &numberList, int difference)
{
    int listSize = numberList.size();
    map<int, int> subsequenceMap;
    int longestLength = 0;

    for (int i = 0; i < listSize; ++i)
    {
        int currentNumber = numberList[i];
        int tempLength = 0;
        auto iterator = subsequenceMap.upper_bound(currentNumber - difference - 1);
