using namespace std;

int daysTogether(const string &arrivalDateAlice, const string &departureDateAlice, const string &arrivalDateBob, const string &departureDateBob)
{
    int daysInMonth[]= {0, 31, 28, 31, 30, 31, 30, 31, 31, 30, 31, 30, 31};
    auto dateToDays= [&](const string &date)
    {
        int month= stoi(date.substr(0, 2));
        int day= stoi(date.substr(3, 2));
        int totalDays= day;
        for(int i= 1; i < month; ++i)
        {
            totalDays+= daysInMonth[i];
        }
        return totalDays;
    };

    int arriveDaysAlice= dateToDays(arrivalDateAlice);
    int leaveDaysAlice= dateToDays(departureDateAlice);
    int arriveDaysBob= dateToDays(arrivalDateBob);
    int leaveDaysBob= dateToDays(departureDateBob);

    int commonStartDate= max(arriveDaysAlice, arriveDaysBob);
    int commonEndDate= min(leaveDaysAlice, leaveDaysBob);

    return max(0, commonEndDate - commonStartDate + 1);
}

