vector<int> findGoodIndices(const vector<int> &numberList, int k)
{
    vector<int> goodIndices;
    for(int currentIndex= k; currentIndex < numberList.size() - k; ++currentIndex)
    {
        bool isLeftNonIncreasing= true;
        bool isRightNonDecreasing= true;

        for(int j= 0; j < k - 1; ++j)
        {
            if(numberList[currentIndex - j - 1] < numberList[currentIndex - j - 2])
            {
                isLeftNonIncreasing= false;
                break;
            }
        }

        for(int j= 0; j < k - 1; ++j)
        {
            if(numberList[currentIndex + j + 1] < numberList[currentIndex + j + 2])
            {
                isRightNonDecreasing= false;
                break;
            }
        }

        if(isLeftNonIncreasing && isRightNonDecreasing)
        {
            goodIndices.push_back(currentIndex);
        }
    }
    return goodIndices;
}

