void depthFirstSearch(int currentNode, int parentNode, const vector<vector<int>> &adjacencyList, const vector<int> &values, int currentMaxValue, int &goodPathCount)
{
    if(values[currentNode] >= currentMaxValue)
    {
        if(values[currentNode] > currentMaxValue)
        {
            goodPathCount++;
            currentMaxValue = values[currentNode];
        }
        for(int j = 0; j < adjacencyList[currentNode].size(); ++j)
        {
            int nextNode = adjacencyList[currentNode][j];
            if(nextNode != parentNode)
            {
                depthFirstSearch(nextNode, currentNode, adjacencyList, values, currentMaxValue, goodPathCount);
            }
        }
    }
}

int numberOfGoodPaths(const vector<int> &values, const vector<vector<int>> &edges)
{
