    vector<vector<int>> adjacencyList(numberOfNodes);
    for (int i = 0; i < edges.size(); ++i)
    {
        adjacencyList[edges[i][0]].push_back(edges[i][1]);
        adjacencyList[edges[i][1]].push_back(edges[i][0]);
    }
    int goodPathCount = 0;
    for (int i = 0; i < numberOfNodes; i++)
    {
        depthFirstSearch(i, -1, adjacencyList, values, -1, goodPathCount);
    }
    return goodPathCount;
}

