    while(leftIndex < rightIndex)
    {
        if(numberList[leftIndex] == numberList[rightIndex])
        {
            leftIndex++;
            rightIndex--;
        }
        else if(numberList[leftIndex] < numberList[rightIndex])
        {
            numberList[leftIndex + 1]+= numberList[leftIndex];
            leftIndex++;
            operationCount++;
        }
        else
        {
            numberList[rightIndex - 1]+= numberList[rightIndex];
            rightIndex--;
            operationCount++;
        }
    }
    return operationCount;
}

