bool canEqualFrequency(string inputWord)
{
    unordered_map<char, int> characterFrequency;
    for (char currentCharacter : inputWord)
    {
        characterFrequency[currentCharacter]++;
    }

    unordered_map<int, int> frequencyCount;
    for (auto it = characterFrequency.begin(); it != characterFrequency.end(); ++it)
    {
        frequencyCount[it->second]++;
    }

    if (frequencyCount.size() != 2)
    {
        return false;
    }

    auto currentIterator = frequencyCount.begin();
    auto &[firstFrequency, firstCount] = *currentIterator;
    currentIterator++;
    auto &[secondFrequency, secondCount] = *currentIterator;

    return (firstCount == 1 && (firstFrequency - 1 == secondFrequency || firstFrequency == 1)) ||
           (secondCount == 1 && (secondFrequency - 1 == firstFrequency || secondFrequency == 1));
}

