int maxTastiness(vector<int> &fruitPrices, vector<int> &fruitTastiness, int maxAmount, int maxCoupons)
{
    int numberOfFruits= fruitPrices.size();
    vector<vector<vector<int>>> dynamicProgrammingTable(numberOfFruits + 1, vector<vector<int>>(maxAmount + 1, vector<int>(maxCoupons + 1, 0)));

    for(int i= 1; i <= numberOfFruits; i++)
    {
        int currentFruitPrice= fruitPrices[i - 1];
        int currentFruitTastiness= fruitTastiness[i - 1];
        int halfFruitPrice= currentFruitPrice / 2;

        for(int j= 0; j <= maxAmount; j++)
        {
            for(int k= 0; k <= maxCoupons; k++)
            {
                dynamicProgrammingTable[i][j][k]= dynamicProgrammingTable[i - 1][j][k];
                if(j >= currentFruitPrice)
                {
                    dynamicProgrammingTable[i][j][k]= max(dynamicProgrammingTable[i][j][k], dynamicProgrammingTable[i - 1][j - currentFruitPrice][k] + currentFruitTastiness);
                }
                if(k > 0 && j >= halfFruitPrice)
                {
                    dynamicProgrammingTable[i][j][k]= max(dynamicProgrammingTable[i][j][k], dynamicProgrammingTable[i - 1][j - halfFruitPrice][k - 1] + currentFruitTastiness);
                }
            }
        }
    }

    return dynamicProgrammingTable[numberOfFruits][maxAmount][maxCoupons];
}

