int calculateWays(vector<vector<int>> &grid, int rows, int columns, int currentRow, int currentColumn, int divisor, int currentSum, vector<vector<vector<int>>> &dp);

int countWaysToReachDivisibleSum(vector<vector<int>> &grid, int divisor)
{
    int rows= grid.size(), columns= grid[0].size();
    vector<vector<vector<int>>> dp(rows, vector<vector<int>>(columns, vector<int>(divisor, -1)));
    return calculateWays(grid, rows, columns, 0, 0, divisor, 0, dp);
}

int calculateWays(vector<vector<int>> &grid, int rows, int columns, int currentRow, int currentColumn, int divisor, int currentSum, vector<vector<vector<int>>> &dp)
{
    if(currentRow == rows - 1 && currentColumn == columns - 1)
    {
        return (currentSum + grid[currentRow][currentColumn]) % divisor == 0;
    }
    if(dp[currentRow][currentColumn][currentSum] == -1)
    {
        int totalWays= 0;
        if(currentRow + 1 < rows)
            totalWays= (totalWays + calculateWays(grid, rows, columns, currentRow + 1, currentColumn, divisor, (currentSum + grid[currentRow][currentColumn]) % divisor, dp)) % 1000000007;
        if(currentColumn + 1 < columns)
            totalWays= (totalWays + calculateWays(grid, rows, columns, currentRow, currentColumn + 1, divisor, (currentSum + grid[currentRow][currentColumn]) % divisor, dp)) % 1000000007;
        dp[currentRow][currentColumn][currentSum]= totalWays;
    }
    return dp[currentRow][currentColumn][currentSum];
}

