vector<int> getPowers(int inputNumber)
{
    vector<int> powerValues;
    while(inputNumber > 0)
    {
        powerValues.push_back(inputNumber & (-inputNumber));
        inputNumber-= inputNumber & (-inputNumber);
    }
    return powerValues;
}

vector<int> productOfPowers(int inputNumber, vector<vector<int>> &queries)
{
    vector<int> powerValues = getPowers(inputNumber);
    vector<int> results;

    for (int i = 0; i < queries.size(); ++i)
    {
        auto query = queries[i];
        longLong productResult = 1;
        for (int index = query[0]; index <= query[1]; index++)
        {
            productResult = (productResult * powerValues[index]) % modulus;
        }
        results.push_back(productResult);
    }

    return results;
}

