int depthFirstSearch(int currentNode, int parentNode, vector<int> &nodeValues, vector<vector<int>> &adjacencyList, int totalSum, int &edgeCount)
{
    int subtreeSum = nodeValues[currentNode];
    for (int i = 0; i < adjacencyList[currentNode].size(); ++i)
    {
        int neighborNode = adjacencyList[currentNode][i];
        if (neighborNode != parentNode)
        {
            subtreeSum += depthFirstSearch(neighborNode, currentNode, nodeValues, adjacencyList, totalSum, edgeCount);
        }
    }
    if (subtreeSum == totalSum / 2)
    {
        edgeCount++;
        return 0;
    }
    return subtreeSum;
}

int deleteEdgesToBalanceTree(vector<int> &nodeValues, vector<vector<int>> &edges)
{
    int numberOfNodes = nodeValues.size();
    vector<vector<int>> adjacencyList(numberOfNodes);
    for (auto &edge : edges)
    {
        adjacencyList[edge[0]].push_back(edge[1]);
        adjacencyList[edge[1]].push_back(edge[0]);
    }

    int totalSum = 0;
    for (int i = 0; i < nodeValues.size(); ++i)
    {
        totalSum += nodeValues[i];
    }
    if (totalSum % 2 != 0)
    {
        return 0; // It is impossible to split the tree fairly.
    }

    int edgeCount = 0;
    depthFirstSearch(0, -1, nodeValues, adjacencyList, totalSum, edgeCount);
    return edgeCount - 1;
}

